const sizeMap = {
  sm: { width: 40, height: 46 },
  md: { width: 47, height: 53 },
  lg: { width: 52, height: 60 }
};


export default {
    name: "SvgMapMarker",
    props: {
        rollSize: {
            type: String,
            default: "sm",
            validator: (val) => ["sm", "md", "lg"].includes(val)
        },
        balance: {
            type: String,
            default: "neutral",
            validator: (val) => ["positive", "negative", "neutral", "unavailable"].includes(val)
        },
        phase: {
            type: String,
            default: "A",
            validator: (val) => ["A", "P", "S", "N", "O", "16"].includes(val)
        },
        specialSchool: {
            type: Boolean,
            default: false
        },
        hexColor: {
            type: String,
            default: "#ffffff"
        }
    },
    computed: {
        svgWidth() {
            return sizeMap[this.rollSize]?.width || sizeMap.sm.width;
        },
        svgHeight() {
            return sizeMap[this.rollSize]?.height || sizeMap.sm.height;
        }
    }
};
</script>